import{auth}from"./firebase";import{GoogleAuthProvider,createUserWithEmailAndPassword,sendEmailVerification,sendPasswordResetEmail,signInWithEmailAndPassword,updatePassword,signInWithPopup}from"firebase/auth";export const doCreateUserWithEmailAndPassword=async(email,password)=>createUserWithEmailAndPassword(auth,email,password);export const doSignInWithEmailAndPassword=async(email,password)=>signInWithEmailAndPassword(auth,email,password);export const doSignInWithGoogle=async()=>{const provider=new GoogleAuthProvider,result=await signInWithPopup(auth,provider);return result};export const doSignOut=()=>auth.signOut();